export const loadMenu = (map) => {
  const nowMapLoaded = new Promise((resolve, reject) => {
      const services = map.querySourceFeatures('composite', {sourceLayer: 'refugees-services'});
      resolve((services) => this.setState({ services: services }));
      reject(console.log('load menu rejected'));
  })
};



export function getResourceObject(services, categoriesList){
  console.log('typeof services', typeof(services), 'typeof cateogries list', typeof(categoriesList)); 
  var resourceObject = categoriesList.map(function(i){ 
      var resource = services[i].properties; 
      resource.coordinates = services[i].geometry.coordinates; 
      resource.lngLat = services[i].lngLat; 
      return resource; 
    }); 
  return resourceObject; 
}

// get the types from mapbox data
export function getCategories(services){
  return services.map(function(a){ return a.type })
    .filter(function(value, index, self){    
      return self.indexOf(value) === index ; 
    })
};


