import React from 'react'; 
// import mapboxgl from 'mapbox-gl'
import { style } from './style.js';

export const resourceObjects = [
        {'name': 'name1',
        'id': 'id1', 
        'website': 'website', 
        'bio': 'bio',
        'telephone': 'telephone', 
        'type': 'type1',
        'coordinates': 'coordinates',
        'onClick': 'onClick',
