        'className': 'className'},
        { 'name': 'name4',
         'id': 'id3', 
         'website': 'website', 
         'bio': 'bio',
         'telephone': 'telephone', 
         'type': 'type3',
         'coordinates': 'coordinates',
         'onClick': 'onClick',
         'className': 'className'},
         { 'name': 'name5',
          'id': 'id3', 
          'website': 'website',     
          'bio': 'bio',
          'telephone': 'telephone', 
          'type': 'type3',
          'coordinates': 'coordinates',
          'onClick': 'onClick',
          'className': 'className'},
          { 'name': 'name6',
           'id': 'id3', 
           'website': 'website', 
           'bio': 'bio',
           'telephone': 'telephone', 
           'type': 'type3',
           'coordinates': 'coordinates',
           'onClick': 'onClick',
           'className': 'className'}
]; 

export function TestPattern() {
    return (
        <ul>
            <li>test pattern this is a test pattern</li>
        </ul>
    )
}



export function ResourceListItem(props) {
    return (
        <li style={style.text}><a href="#target" /* onClick={mapboxPopup}*/ >{props.name}</a></li>
    )
}

export const categoryList = makeTypeList(resourceObjects);

function makeTypeList(objectArray){
    return objectArray.map(function(a){ return a.type }).filter(function(value, index, self){
        return self.indexOf(value) === index ; 
    })
}; 



