        <div style={{'display':'flex', 'alignItems': 'center', 'justifyContent': 'start', 'fontSize':'1rem'}}>
            <i>{'icon'}</i>
            <h2 style={style.text}><a href="#target" onClick={this.toggleChildMenu}>{props.category}</a></h2>
            <i>{'chevron'}</i>
        </div>
    )
}


