import React, { Component } from 'react';
import { toggleChildMenu } from './main.js';

export class TopNav extends Component {
    render() {
        return (
            <nav>
                <div className='top-nav'><a href="https://refugeeswelcomehome.org/"><h1>Migrant Service Map</h1></a></div>
                <div id='top-nav' className='top-nav'>
                    <a className='top-nav' href="#target" >Top Nav</a>
                    <a className='top-nav' href="#target" >Top Nav</a>
                    <a className='top-nav' href="#target" >Top Nav</a>
                </div>
            </nav>
        )
    }
}

export const ResourceSectionHeader = (props) => {
    return (
        <div className='section-header'>
            <i>{'icon'}</i>
            <h2><a href="#target" onClick={() => toggleChildMenu(props.category)}>{props.category}</a></h2>
            <i>{'chevron'}</i>
        </div>
    )
}

//the individual resources (company, school, non-profit)
export const ResourceListItem = (resourceListItem) => <li className='list-item'><a href="#target" /* onClick={mapboxPopup}*/ >{resourceListItem.name}</a></li> ;

export const ResourceList = (props) => {
    return(
        <ul className='child-menu' id={props.category} key={props.category}>
            {props.resourcesObject.filter(resource => resource.type === props.category).map(resource => <ResourceListItem name={resource.name} key={resource.name} category={resource.type}/>
            )}
        </ul>
    )
}

export default class Menu extends Component {
    render() {
        // debugger;
        return(
            <ul className='side-menu'>
                <li>
                    <h3>Service Type</h3>
                    <ul>{this.props.categoriesList.map(category => (
                            <div key={category} >
                                <li>
                                    <ResourceSectionHeader category={category} />
                                </li>
                                <li>
                                    <ResourceList resourcesObject={this.props.resourcesObject} category={category} />
                                </li>
                            </div>
                        ))}</ul>
                </li>
                <li>
                    <h3>Other Criteria</h3>
                    <ul>
                        <li><h4>Visa Status</h4></li>
                    </ul>
                </li>
            </ul>
        )
    }

}
