import React, {
    Component
} from 'react';
import mapboxgl from 'mapbox-gl';
import MapboxGeocoder from '@mapbox/mapbox-gl-geocoder';
import ToggleableLayers from './toggleableLayersObjects.js';
import './index.css';
import { setPopup, getCategories } from './map-services.js';
import Menu from './components';


mapboxgl.accessToken = 'pk.eyJ1IjoicmVmdWdlZXN3ZWxjb21lIiwiYSI6ImNqZ2ZkbDFiODQzZmgyd3JuNTVrd3JxbnAifQ.UY8Y52GQKwtVBXH2ssbvgw';


class Map extends React.Component {

    componentDidMount() {
        let map = new mapboxgl.Map({
            container: 'map', // container id
            style: 'mapbox://styles/refugeeswelcome/cjh9k11zz15ds2spbs4ld6y9o', // stylesheet location
            center: [-71.066954, 42.359947], // starting position [lng, lat]
            zoom: 11 // starting zoom
        });

        var categories = getCategories(map); 
        console.log(categories); 

        let geocoder = new MapboxGeocoder({
            accessToken: mapboxgl.accessToken
        });

        map.addControl(geocoder);


        map.on('load', function () {
            // for (var i = 0; i < Object.keys(ToggleableLayers).length; i++) {
            //     var id = ToggleableLayers[i].id;
            //     var idStyle = 

            categories.map(function(i){ map.setLayoutProperty(i, 'visibility', 'none');
            }); 

            map.on('click', function(e, map){return setPopup(e, map)}); 
        }); 

            map.addSource('single-point', {
                "type": "geojson",
                "data": {
                    "type": "FeatureCollection",
                    "features": []
                }
            });
            map.addLayer({
                "id": "point",
                "source": "single-point",
                "type": "circle",
                "paint": {
                    "circle-radius": 10,
                    "circle-color": "#007cbf"
                }
            });
            geocoder.on('result', function (ev) {
                map.removeLayer('circle-outline');
                map.removeLayer('circle-outline-two');
                map.removeLayer('circle-outline-three');
                map.removeLayer('circle-outline-four');
                map.getSource('single-point').setData(ev.result.geometry);
                var center = {
                    "type": "Feature",
                    "properties": {
                        "marker-color": "#0f0"
                    },
                    "geometry": {
                        "type": "Point",
                        "coordinates": ev.result.geometry.coordinates
                    }
                };
                var radius = .5;
                var options = {
                    steps: 100,
                    units: 'miles'
                };
                var circle = MapboxGeocoder.turf.circle(center, radius, options);
                map.addLayer({
                    "id": "circle-outline",
                    "type": "line",
                    "source": {
                        "type": "geojson",
                        "data": circle
                    },
                    "paint": {
                        "line-color": "#046328",
                        "line-opacity": 0.8,
                        "line-width": 10,
                        "line-offset": 5
                    },
                    "layout": {}
                });
                var radiusTwo = 1;
                var circleTwo = MapboxGeocoder.turf.circle(center, radiusTwo, options);
                map.addLayer({
                    "id": "circle-outline-two",
                    "type": "line",
                    "source": {
                        "type": "geojson",
                        "data": circleTwo
                    },
                    "paint": {
                        "line-color": "#00AA46",
                        "line-opacity": 0.8,
                        "line-width": 10,
                        "line-offset": 5
                    },
                    "layout": {}
                });
                var radiusThree = 3;
                var circleThree = MapboxGeocoder.turf.circle(center, radiusThree, options);
                map.addLayer({
                    "id": "circle-outline-three",
                    "type": "line",
                    "source": {
                        "type": "geojson",
                        "data": circleThree
                    },
                    "paint": {
                        "line-color": "#71C780",
                        "line-opacity": 0.8,
                        "line-width": 10,
                        "line-offset": 5
                    },
                    "layout": {}
                });
                var radiusFour = 5;
                var circleFour = MapboxGeocoder.turf.circle(center, radiusFour, options);
                map.addLayer({
                    "id": "circle-outline-four",
                    "type": "line",
                    "source": {
                        "type": "geojson",
                        "data": circleFour
                    },
                    "paint": {
                        "line-color": "#D5EDDB",
                        "line-opacity": 0.8,
                        "line-width": 10,
                        "line-offset": 5
                    },
                    "layout": {}
                });
            });

    };


    componentWillUnmount() {
        this.map.remove();
    }

    render() {
        var style = {
            position: 'absolute',
            top: '100px',
            bottom: '5px',
            width: '50%',
        };
        return (
        <div className = 'map' >
            <div id='map' style={style}
                ref = { function(el){ this.mapContainer = el } } >
            </div> 
            <Menu categoryList={categoryList} map={this.map}/>
        </div>
        )
    };
}; 

export default Map;
