import React from 'react'; 
// import mapboxgl from 'mapbox-gl'

const style = {
    'style': 'object',
    'resource': {'color': 'red', 'listStyle': 'none', 'alignItems': 'left'},
    'text': {'fontSize': '1rem', 
        'color': 'alicewhite', 
        'display':'block', 
        'padding':'1rem',
        'textAlign': 'left'
    }
}

export const resourceObjects = [
        {'name': 'name1',
        'id': 'id1', 
        'website': 'website', 
        'bio': 'bio',
        'telephone': 'telephone', 
        'type': 'type1',
        'coordinates': 'coordinates',
        'onClick': 'onClick',
        'className': 'className'},
        {'name': 'name2',
        'id': 'id2', 
        'website': 'website', 
        'bio': 'bio',
        'telephone': 'telephone', 
        'type': 'type2',
        'coordinates': 'coordinates',
        'onClick': 'onClick',
        'className': 'className'},
       { 'name': 'name3',
        'id': 'id3', 
        'website': 'website', 
        'bio': 'bio',
        'telephone': 'telephone', 
        'type': 'type3',
        'coordinates': 'coordinates',
        'onClick': 'onClick',
        'className': 'className'},
        { 'name': 'name4',
         'id': 'id3', 
         'website': 'website', 
         'bio': 'bio',
         'telephone': 'telephone', 
         'type': 'type3',
         'coordinates': 'coordinates',
         'onClick': 'onClick',
         'className': 'className'},
         { 'name': 'name5',
          'id': 'id3', 
          'website': 'website',     
          'bio': 'bio',
          'telephone': 'telephone', 
          'type': 'type3',
          'coordinates': 'coordinates',
          'onClick': 'onClick',
          'className': 'className'},
          { 'name': 'name6',
           'id': 'id3', 
           'website': 'website', 
           'bio': 'bio',
           'telephone': 'telephone', 
           'type': 'type3',
           'coordinates': 'coordinates',
           'onClick': 'onClick',
           'className': 'className'}
]; 

const icons = {
    'type1':'mb1',
    'type2':'mb2',
    'type3':'mb3',
    'type4': 'mb4'
}; 


export function TestPattern() {
    return (
        <ul>
            <li>test pattern this is a test pattern</li>
        </ul>
    )
}



export function ResourceListItem(props) {
    return (
        <li style={style.text}><a href="#target" /* onClick={mapboxPopup}*/ >{props.name}</a></li>
    )
}

const categoryList = makeTypeList(resourceObjects);

function makeTypeList(objectArray){
    return objectArray.map(function(a){ return a.type }).filter(function(value, index, self){
        return self.indexOf(value) === index ; 
    })
}; 



