import React from 'react'; 
import mapboxgl from 'mapbox-gl'

const style = {
    'style': 'object'
}
const resourceObjects = [
        {'name': 'name',
        'id': 'id', 
        'website': 'website', 
        'bio': 'bio',
        'telephone': 'telephone', 
        'type': 'type',
        'coordinates': 'coordinates',
        'onclick': 'onclick',
        'className': 'className'},
        {'name': 'name',
        'id': 'id', 
        'website': 'website', 
        'bio': 'bio',
        'telephone': 'telephone', 
        'type': 'type',
        'coordinates': 'coordinates',
        'onclick': 'onclick',
        'className': 'className'},
       { 'name': 'name',
        'id': 'id', 
        'website': 'website', 
        'bio': 'bio',
        'telephone': 'telephone', 
        'type': 'type',
        'coordinates': 'coordinates',
        'onclick': 'onclick',
        'className': 'className'}
]; 

const icons = ['mb1','mb2','mb3','mb4']; 


export function TestPattern() {
    return (
        <ul>
            <li>test pattern this is a test pattern</li>
        </ul>
    )
}



function resourceListItem(props) {
    return (
        <li style={style.resource}><a href="#target" onclick={mapboxPopup}>{props.name}</a></li>
    )
}

const categoryList = getCategories(); 

function getCategories(){
    let map = 'mapbox reference'; 
    let services = map.querySourceFeatures('composite', {sourceLayer: 'refugees-services'}); 
    let serviceCategories = []; 
    for (let i = 0 ; i < services ; i++){
        let type = services[i].properties.type; 
        if ( ! Object.keys(serviceCategories).includes(type) ) {
            serviceCategories.push(type); 
        }
    }
}

function resourceSectionHeader(props){
    const category = props.category;
    const icon = icons[category]; 

    function toggleBoth(){ 
        toggleMapIcons(); 
        toggleChildMenu();
    } 

    return (
        <li>
            <i>{icon}</i>
            <h2><a href="#target" onclick={toggleBoth}>{category}</a></h2>
            <chevron>{'chevron'}</chevron>
        </li>
    )
}


function toggleMapIcons(){ 'toggle(category)' };

function toggleChildMenu(){ 'toggle(catoegory)' };


function resourceList(props){
    const style={'scroll': 'scroll'};
    return(
        <ul style={style}>
            {resourceObjects.map(resource => 
                <resourceListItem name={resource.name} onclick={mapboxPopup} key={resource.id} /> 
            )}
        </ul>
    )
}



function menu(props){
    return (
    <ul>
        {categoryList.map(function(){
            return ( 
                <li>
                    <resourceSectionHeader category={props.category} />
                </li>,
                <li>
                    <resourceList props={props} resourceObjectsList={props.resourceObjectsList} />
                </li>
            )
        })}
    </ul>
    )

}



function mapboxPopup() {
    new mapboxgl.Popup()
    .setLngLat(coordinates)
    .setHTML('<h4>' + name + '</h4>' + '<a href=' + website + '>' + website + '</a>' + '<br><br>' + '<i>' + bio + '</i>' + '<br><br><b>Telephone: </b>' + telephone)
    .addTo(map);
}
