export function ResourceSectionHeader(props){

    function toggleBoth(){ 
        toggleMapIcons(); 
        toggleChildMenu();
    } 

    return (
        <li style={style.resource}>
            <span>{'icon'}</span>
            <h2><a href="#target">{props.category}</a></h2>
            <i>{'chevron'}</i>
        </li>
    )
}


function toggleMapIcons(){ 'toggle(category)' };

function toggleChildMenu(){ 'toggle(catoegory)' };


export function ResourceList(props){
    const style={'scroll': 'scroll'};
    return(
        <ul>
        {resourceObjects.filter(function(resource){console.log(resource.type, props.category); return resource.type === props.category})
            .map(function(resource) {console.log(resource.name, resource.id) ; return <ResourceListItem name={resource.name} key={resource.id} category={resource.type}/>
        })}
        </ul>
    )
}


export function Menu(props){
    return (
    <ul>
        {categoryList.map(function(a){
            return ( 
                <li>
                    <ResourceSectionHeader category={a} />
                </li>,
                <li>
                    <ResourceList resourceObjects={resourceObjects} category={a}/>
                </li>
            )
        })}
    </ul>
    )

}



