import React from 'react'; 
// import mapboxgl from 'mapbox-gl'

const style = {
    'style': 'object',
    'resource': {'color': 'red', 'listStyle': 'none'},

}
export const resourceObjects = [
        {'name': 'name',
        'id': 'id1', 
        'website': 'website', 
        'bio': 'bio',
        'telephone': 'telephone', 
        'type': 'type1',
        'coordinates': 'coordinates',
        'onClick': 'onClick',
        'className': 'className'},
        {'name': 'name',
        'id': 'id2', 
        'website': 'website', 
        'bio': 'bio',
        'telephone': 'telephone', 
        'type': 'type2',
        'coordinates': 'coordinates',
        'onClick': 'onClick',
        'className': 'className'},
       { 'name': 'name',
        'id': 'id3', 
        'website': 'website', 
        'bio': 'bio',
        'telephone': 'telephone', 
        'type': 'type3',
        'coordinates': 'coordinates',
        'onClick': 'onClick',
        'className': 'className'}
]; 

const icons = {
    'type1':'mb1',
    'type2':'mb2',
    'type3':'mb3',
    'type4': 'mb4'
}; 


export function TestPattern() {
    return (
        <ul>
            <li>test pattern this is a test pattern</li>
        </ul>
    )
}



export function ResourceListItem(props) {
    return (
        <li style={style.resource}><a href="#target" /* onClick={mapboxPopup}*/ >{props.name}</a></li>
    )
}

const categoryList = resourceObjects.map(a => a.type);  // getCategories(); 

