export function ResourceSectionHeader(props){
    const category = props.type;
    const icon = icons[category]; 

    function toggleBoth(){ 
        toggleMapIcons(); 
        toggleChildMenu();
    } 

    return (
        <li>
            <i>{icon}</i>
            <h2><a href="#target">{category}</a></h2>
            <i>{'chevron'}</i>
        </li>
    )
}


function toggleMapIcons(){ 'toggle(category)' };

function toggleChildMenu(){ 'toggle(catoegory)' };


export function ResourceList(props){
    const style={'scroll': 'scroll'};
    return(
        <ul style={style}>
        {categoryList[props.category].map(resource => 
            <ResourceListItem name={resource.name} key={resource.id} />
        )}
        </ul>
    )
}


export function Menu(props){
    return (
    <ul>
        {categoryList.map(function(a){
            return ( 
                <li>
                    <ResourceSectionHeader category={a.type} />
                </li>,
                <li>
                    <ResourceList resourceObjects={resourceObjects} />
                </li>
            )
        })}
    </ul>
    )

}



