import React from 'react'; 
// import mapboxgl from 'mapbox-gl'

const style = {
    'style': 'object',
    'resource': {'color': 'red', 'listStyle': 'none'},

}
export const resourceObjects = [
        {'name': 'name',
        'id': 'id', 
        'website': 'website', 
        'bio': 'bio',
        'telephone': 'telephone', 
        'type': 'type',
        'coordinates': 'coordinates',
        'onClick': 'onClick',
        'className': 'className'},
