        'id': 'id', 
        'website': 'website', 
        'bio': 'bio',
        'telephone': 'telephone', 
        'type': 'type',
        'coordinates': 'coordinates',
        'onClick': 'onClick',
        'className': 'className'},
       { 'name': 'name',
        'id': 'id', 
        'website': 'website', 
        'bio': 'bio',
        'telephone': 'telephone', 
        'type': 'type',
        'coordinates': 'coordinates',
        'onClick': 'onClick',
        'className': 'className'}
]; 

const icons = {
    'type':'mb1',
    'type2':'mb2',
    'type3':'mb3',
    'type4': 'mb4'
}; 


export function TestPattern() {
    return (
        <ul>
            <li>test pattern this is a test pattern</li>
        </ul>
    )
}



export function ResourceListItem(props) {
    return (
        <li style={style.resource}><a href="#target" /* onClick={mapboxPopup}*/ >{props.name}</a></li>
    )
}

const categoryList = resourceObjects;  // getCategories(); 

